const reducer = (state, action) => {
  switch(action.type) {
    case 'CLEAR_CART':
      return {
        ...state, cart: []
      }
      break;
    case 'REMOVE_CART_ITEM':
      return {
        ...state, cart: cart.filter(item => (item.id != id))
      }
      break;
    default:
      return state;
  }
  return state;
}

export default reducer;
