const reducer = (state, action) => {
  switch(action.type) {
    case 'CLEAR_CART':
      return {
        ...state, cart: []
      }
      break;
    case 'REMOVE_CART_ITEM':
      return {
        ...state, cart: state.cart.filter(item => (item.id !== action.payload))
      }
      break;
    default:
      return state;
  }
}

export default reducer;
