import React from 'react';

const Menu = ({ items }) => {

  const menuItems = items.map(item => {
    const { id, title, category, price, img, desc } = item;
    return (
      <article key={id}>
        <img
          className='photo'
          src={img}
          alt={title}
        />
        <div className='item-info'>
          <header>
            <h4>{title}</h4>
            <h4>{price}</h4>
          </header>
        </div>
      </article>
    );
  });

  return (
    <div className='section-center'>
      {menuItems}
    </div>
  );
};

export default Menu;
