import React, { useState, useEffect } from 'react'
import List from './List'
import Alert from './Alert'

function App() {

  const [ name, setName ] = useState('');
  const [ list, setList ] = useState([]);
  const [ isEditable, setIsEditable ] = useState(false);
  const [ editId, setSetId ] = useState(null);
  const [ alert, setAlert ] = useState({ isShowing: false, msg: '', type: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('hello');
  }

  const handleGroceryInput = (e) => {
    e.preventDefault();
    let groceryName = e.target.value;
    setName(groceryName);
  }

  return (
    <section className='section-center'>
      <form
        className='grocery-form'
        onSubmit={handleSubmit}
      >
        {alert.isShowing && <Alert />}
        <h3>Grocery Bud</h3>
        <div className='form-control'>
          <input
            className='grocery'
            type='text'
            placeholder='e.g eggs'
            value={name}
            onChange={handleGroceryInput}
          />
          <button
            className='submit-btn'
            type='submit'
          >
            {isEditable ? 'Edit' : 'Submit'}
          </button>
        </div>
      </form>
      <div className='grocery-container'>
        <List/>
        <button className='clear-btn'>
          Clear Items
        </button>
      </div>
    </section>  
  )
}

export default App
